import { useState } from "react";
import { useDispatch,useSelector } from "react-redux";
import { Grid, Typography } from '@mui/material';
import TextField from '@mui/material/TextField';


import { generateId, selectById } from "../Todo/todoSlice";
import { todoAdd } from "../Todo/todoSlice";
import { Footer } from "../Footer/footer";
import { posturl } from "../../api/api";
export const Header = () => {

    const dispatch = useDispatch();

    const [text, setText] = useState('');
    const id = useSelector(generateId);





    const changeHandler = (e) => {
        setText(e.target.value);
    }

    // const handleRemoveCompletedClick = () => {
    //   dispatch(todoClearComplete());
    // }






    // const handleAllCompleteClick = () => {
    //   dispatch(todoAllComplete())
    // }





    const handleKeyDown =  (e) => {
        // If the user pressed the Enter key:
        const trimmedText = text.trim()
        if (e.which === 13 && trimmedText) {
          // Dispatch add action creator function 
          dispatch(todoAdd(text));
          // And clear out the text input
          setText('')

        }
      }



    return(
    <div>
        <br/>

    <Grid container rowSpacing={2} direction='column'>
      <Grid item container spacing={2}>
        <Grid item xs={1} lg={3}/>
        <Grid item >
          <Typography variant="h3">To Do Lists</Typography>
        </Grid>

      </Grid>
      <Grid item container>
        <Grid item xs ={1} lg={3}/>


        <Grid item xs={5} lg={2}>
        <TextField id="standard-basic" label="Please enter you want to do..." variant="standard" value={text} onChange={changeHandler} onKeyDown={handleKeyDown} />

        </Grid>
        <Grid item xs = {6} lr={6}></Grid>
        <Grid item xs={1} lg={3}></Grid>
        <Grid item xs ={11} lg={3} zeroMinWidth>
            <Typography style={{overflowWrap: 'break-word'}}>{text}</Typography>
        </Grid>
      </Grid>
    </Grid>

    </div>
    )
}
